import { React, useState, useEffect } from 'react';
import Menu from "../../components/menu/menu"
import Footer from "../../components/footer/footer"
import "./estoque.css"


import BuscaBD from '../fetchBackEnd/api';

import { makeStyles } from '@material-ui/core/styles';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import Button from '@material-ui/core/Button';





export default function Estoque(props) {
    const useStyles = makeStyles((theme) => ({
        root: {
            '& > *': {
                margin: theme.spacing(1),
                width: '25ch',
            },
            button: {
                margin: theme.spacing(1),

            },
            '& > *': {
                margin: theme.spacing(1),
            },
        },
    }));
    const classes = useStyles();


    const [selectFiltro, setSelectFiltro] = useState({
        BDBlindado: false,
        BDMarca: false,
        BDAno: false,
        BDCombustivel: false,
        selectBlindado: "todos", //colocado como string, pq a opcao NÃ£o boolean ja possui esse valor
        selectMarca: false,
        selectValor: false,
        selectAno: false,
        selectCambio: false,
        selectCombustivel: false
