import { React, useState } from 'react';
import BuscaBD from "../fetchBackEnd/api"  //importa classe para o axios

import "./painelAdministrativo.css"
// import Formulario from "./publicacao/publicacao"
import Formulario from "./formulario/formulario"

import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import DeleteIcon from '@material-ui/icons/Delete';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import SendIcon from '@material-ui/icons/Send';
import SaveIcon from '@material-ui/icons/Save';
import TextField from '@material-ui/core/TextField';



const useStyles = makeStyles((theme) => ({
    button: {
        margin: theme.spacing(1),
    },
}));


export default function Paineladministrativo() {
    const classes = useStyles();

    //const para renderização dos formularios
    const [criarAnuncio, setCriarAnuncio] = useState(false)
    const [alterarAnuncio, setAlterarAnuncio] = useState(false)
    const [deletarAnuncio, setDeletarAnuncio] = useState(false)

    const [editaFormulario, setEditaFormulario] = useState(
        {
            campoIdPesquisa: "",
            retornoPesquisaBD: {}
        }
    )
    //armazena dados do texto para enviar para busca no BD




    function SelecionaFormulario(dadosBotao, recebe) {
        if (recebe === "criarAnuncio") {
            setCriarAnuncio(true)
            setAlterarAnuncio(false)
            setDeletarAnuncio(false)
        }
        if (recebe === "alterarAnuncio") {
            setCriarAnuncio(false)
            setAlterarAnuncio(true)
            setDeletarAnuncio(false)
        }
        if (recebe === "deletarAnuncio") {
            setCriarAnuncio(false)
            setAlterarAnuncio(false)
            setDeletarAnuncio(true)
        }
    }

    //funcão busca no bd pela palavra
    async function BuscarBDDados() {
        const classBuscaBD = new BuscaBD()
        const resultado = await classBuscaBD.BuscaBDGetDados(buscaParaAlterar)
        setEditaFormulario((prevState => {
            ({ ...prevState, retornoPesquisaBD: resultado })
        }))

    }

    //em construção
    function AtualizarDadosBd() {

    }

    return (
        <>

            <div className="paineladministrativo-div">
                <h2>Bem vindo ao PAINEL ADMINISTRATIVO DE OFERTAS</h2>
            </div>


            <article className="paineladministrativo-article">

                <h4>SELECIONE A OPÇÃO DESEJADA:</h4>


                <div className="paineladministrativo-article-div-button">
                    <Button
                        onClick={(dadosBotao, envia = "criarAnuncio") => { SelecionaFormulario(dadosBotao, envia) }}
                        variant="contained"
                        color="primary"
                        className={classes.button}
                        startIcon={<SendIcon />}
                    >
                        CRIAR ANÚNCIO
                    </Button>

                    <Button
                        onClick={(dadosBotao, envia = "alterarAnuncio") => { SelecionaFormulario(dadosBotao, envia) }}
                        variant="contained"
                        color="default"
                        className={classes.button}
                        startIcon={<CloudUploadIcon />}
                    >
                        ALTERAR ANÚNCIO
                    </Button>

                    <Button
                        onClick={(dadosBotao, envia = "deletarAnuncio") => { SelecionaFormulario(dadosBotao, envia) }}
                        variant="contained"
                        color="secondary"
                        className={classes.button}
                        startIcon={<DeleteIcon />}
                    >
                        DELETAR ANÚNCIO
                    </Button>
                </div>
            </article>

            <div className="seletorformulario">

                {criarAnuncio &&
                    <>
                        <h2>PUBLICADOR DE ANÚNCIOS</h2>
                        <Formulario tipoFormulario="criarAnuncio" />
                        < Button
                            type="submit"
                            variant="contained"
                            color="primary"
                            size="large"
                            className={classes.button}
                            startIcon={<SaveIcon />}
                        >
                            PUBLICAR
                        </Button>
                    </>
                }

            </div>
            <div className="seletorformulario">

                {alterarAnuncio &&
                    <>
                        <div className="publicacao-div-search">
                            <TextField
                                onBlur={(recebe) => {
                                    setEditaFormulario((prevState) => { return { ...prevState, campoIdPesquisa } })
                                }}
                                style={{ margin: "0 1% 3% 10%", }}
                                label="BUSCAR POR ID"
                                id="outlined-size-small"
                                defaultValue=""
                                variant="outlined"
                                size="small"
                                color="success"
                            />
                            <Button
                                onClick={() => { BuscarBDDados() }}
                                style={{ margin: "0", backgroundColor: "white" }}
                                variant="contained"
                                color="inherit"
                                className={classes.button}
                                startIcon={<i class="fas fa-search fa-2x"></i>}
                            >
                                BUSCAR ANÚNCIO
                        </Button>
                        </div>


                        <h2>EDIÇÃO DE ANÚNCIOS</h2>

                        <Formulario tipoFormulario="alterarAnuncio" />
                        <Button
                            onClick={() => { AtualizarDadosBd() }}
                            variant="contained"
                            color="default"
                            className={classes.button}
                            startIcon={<CloudUploadIcon />}
                        >
                            ALTERAR ANÚNCIO
                        </Button>
                    </>
                }

            </div>
            <div className="seletorformulario">

                {deletarAnuncio &&
                    <>
                        <h2>REMOVER ANÚNCIOS</h2>
                        <Formulario tipoFormulario="deletarAnuncio" />
                        <Button
                            variant="contained"
                            color="secondary"
                            className={classes.button}
                            startIcon={<DeleteIcon />}
                        >
                            REMOVER ANÚNCIO
                        </Button>
                    </>
                }

            </div>

            {/* <div className="animacao">

                {alterarAnuncio && <AlteracaoPublicacao />}

            </div> */}
        </>

    )

}
