import { React, useState, useEffect } from 'react';
import Menu from "../../components/menu/menu"
import Footer from "../../components/footer/footer"
import "./estoque.css"


import BuscaBD from '../fetchBackEnd/api';

import { makeStyles } from '@material-ui/core/styles';
import { TextField, Button } from '@material-ui/core/';
import SearchIcon from '@material-ui/icons/Search';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';




export default function Estoque(props) {
    const useStyles = makeStyles((theme) => ({
        root: {
            '& > *': {
                margin: theme.spacing(1),
                width: '25ch',
            },
            button: {
                margin: theme.spacing(1),

            },
        },
    }));
    const classes = useStyles();

    // const [carrosEstoque, setCarrosEstoque] = useState(false)
    // const [selectFiltro, setCarrosEstoque] = useState({})

    const [carrosEstoque, setCarrosEstoque] = useState({
        todosDestaques: false,
        paginacao: 0,
        paginaAvanca: 8,
        paginaRetorna: 0,
        ativaBotaoEsquerdo: false,
        ativaBotaodireito: false
    })

    useEffect(async () => {
        const classBuscaBD = new BuscaBD
        const resultado = await classBuscaBD.Estoque()
        // setCarrosEstoque(resultado)
        setCarrosEstoque(prevState => {
            return { ...prevState, todosDestaques: resultado.data, paginacao: resultado.data.slice(carrosEstoque.paginaRetorna, carrosEstoque.paginaAvanca) }
        })
    })
    function Paginacao(direcao) {
        if (direcao === "avancar") {
            if (imagensDestaque.paginaAvanca >= imagensDestaque.todosDestaques.length) { return }
            setImagensDestaque(prevState => {
                return { ...prevState, paginacao: imagensDestaque.todosDestaques.slice(imagensDestaque.paginaRetorna + 8, imagensDestaque.paginaAvanca + 8), paginaAvanca: imagensDestaque.paginaAvanca + 8, paginaRetorna: imagensDestaque.paginaRetorna + 8 }
            })
        } else {
            setImagensDestaque(prevState => {
                return { ...prevState, paginacao: imagensDestaque.todosDestaques.slice(imagensDestaque.paginaRetorna - 8, imagensDestaque.paginaAvanca - 8), paginaAvanca: imagensDestaque.paginaAvanca - 8, paginaRetorna: imagensDestaque.paginaRetorna - 8 }
            })
        }
    }

    function MostrarTopoDestaque() {
        window.location.href = ("#destaques")
    }

    return (
        <>
            <Menu />

            <menu className="estoque-menu-left">
                <h3 className="titulo-estoque">VEÍCULOS EM ESTOQUE</h3>
                <div className="estoque-menu-left-div">
                    <div className="estoque-menu-left-div-div">
                        <label for="blindado">Veículos Blindados</label>
                        <select id="blindado">
                            <option >TODOS</option>
                            <option >SIM</option>
                            <option>NÃO</option>
                        </select>
                    </div>
                    <div className="estoque-menu-left-div-div">
                        <label for="blindado">Marca</label>
                        <select id="blindado">
                            <option >TODOS</option>
                            <option >SIM</option>
                            <option>NÃO</option>
                        </select>
                    </div>
                    <div className="estoque-menu-left-div-div">
                        <label for="blindado">Preço</label>
                        <select id="blindado">
                            <option >TODOS</option>
                            <option >SIM</option>
                            <option>NÃO</option>
                        </select>
                    </div>
                    <div className="estoque-menu-left-div-div">
                        <label for="blindado">Ano</label>
                        <select id="blindado">
                            <option >TODOS</option>
                            <option >SIM</option>
                            <option>NÃO</option>
                        </select>
                    </div>
                    <div className="estoque-menu-left-div-div">
                        <label for="blindado">Câmbio</label>
                        <select id="blindado">
                            <option >TODOS</option>
                            <option >SIM</option>
                            <option>NÃO</option>
                        </select>
                    </div>
                    <div className="estoque-menu-left-div-div">
                        <label for="blindado">Combustível</label>
                        <select id="blindado">
                            <option >TODOS</option>
                            <option >SIM</option>
                            <option>NÃO</option>
                        </select>
                    </div>
                    <div className="estoque-menu-left-div-button">
                        <Button variant="contained" size="large" color="primary" >
                            BUSCAR
                        </Button>
                    </div>
                </div>
                <div className="estoque-article" >
                    <h1>Anúncios</h1>
                    <div className="estoque-article-estoque">
                        {carrosEstoque &&
                            carrosEstoque.data.map((recebe, index) => {
                                const dados = { ...recebe, imagensPath: JSON.parse(recebe.imagensPath) }
                                return (
                                    <div className="estoque-article-estoque-div">
                                        <div className="estoque-article-estoque-div-imagem">
                                            <a href={"http://192.168.0.150:9000/static/" + dados.imagensPath[0]} target="_blank">
                                                <img alt={dados.modelo} key={index} src={"http://192.168.0.150:9000/static/" + dados.imagensPath[0]}></img>
                                            </a>
                                        </div>
                                        <div className="estoque-article-estoque-div-imagem-info">
                                            <span className="destaques-article-div-grid-div-p">{dados.modelo}</span>
                                            <hr />
                                            <span className="destaques-article-div-grid-div-p-p">R$ {dados.valor},00</span>
                                            <hr />
                                            <span className="destaques-article-div-grid-div-p-p-p">{dados.motor}</span>
                                            <span className="destaques-article-div-grid-div-p-p-p"> {dados.combustivel}</span>
                                            <span className="destaques-article-div-grid-div-p-p-p">{dados.porta}P {dados.cambio}</span>
                                            <span className="destaques-article-div-grid-div-p-p-p">{dados.ano}</span>
                                        </div>

                                    </div>
                                )
                            })

                        }


                    </div>

                </div>
                <div className="destaques-article-div-navegacao">
                    <Button
                        onClick={(click, direcao = "voltar") => {
                            MostrarTopoDestaque()
                            Paginacao(direcao)
                        }}
                        style={{ marginLeft: "20px" }}
                        variant="contained"
                        color="primary"
                        className={classes.button}
                        startIcon={<ArrowBackIosIcon style={{ fontSize: 28, marginLeft: "20px" }} />}
                        disabled={carrosEstoque.paginaRetorna <= 0}
                    >

                    </Button>
                    <Button
                        onClick={(click, direcao = "avancar") => {
                            Paginacao(direcao)
                            MostrarTopoDestaque()
                        }}
                        style={{ marginLeft: "20px" }}
                        variant="contained"
                        color="primary"
                        className={classes.button}
                        startIcon={<ArrowForwardIosIcon style={{ fontSize: 28, marginLeft: "20px" }} />}
                        disabled={carrosEstoque.paginaAvanca >= carrosEstoque.todosDestaques.length}
                    >

                    </Button>
                </div>

            </menu >
            <Footer />
        </>

    )
}
